INSERT INTO met.population(code,name,documentation) VALUES('ausabr','Aboriginal Australian','Includes individuals who either self-report or have been described by authors as Australian Aboriginal. These are expected to be descendants of early human migration into Australia from Eastern Asia and can be distinguished from other Asian populations by mtDNA and Y chromosome variation.');
INSERT INTO met.population(code,name,documentation) VALUES('amcarafr','African American or Afro-Caribbean','Includes individuals who either self-report or have been described by authors as African American or Afro-Caribbean. This category also includes individuals who genetically cluster with reference populations from this region, for example, 1000 Genomes and/or HapMap ACB or ASW populations. We note that there is likely to be significant admixture with European ancestry populations.');
INSERT INTO met.population(code,name,documentation) VALUES('afr','African unspecified','Includes individuals that either self-report or have been described as African, but there was not sufficient information to allow classification as African American, Afro-Caribbean or Sub-Saharan African.');
INSERT INTO met.population(code,name,documentation) VALUES('cas','Central Asian','Includes individuals who either self-report or have been described by authors as Central Asian. We note that there does not appear to be a suitable reference population for this population and efforts are required to fill this gap.');
INSERT INTO met.population(code,name,documentation) VALUES('eas','East Asian','Includes individuals who either self-report or have been described by authors as East Asian or one of the sub-populations from this region (e.g., Chinese). This category also includes individuals who genetically cluster with reference populations from this region, for example, 1000 Genomes and/or HapMap CDX, CHB, CHS, and JPT populations.');
INSERT INTO met.population(code,name,documentation) VALUES('eur','European','Includes individuals who either self-report or have been described by authors as European, Caucasian, white, or one of the sub-populations from this region (e.g., Dutch). This category also includes individuals who genetically cluster with reference populations from this region, for example, 1000 Genomes and/or HapMap CEU, FIN, GBR, IBS, and TSI populations.');
INSERT INTO met.population(code,name,documentation) VALUES('gme','Greater Middle Eastern (Middle Eastern, North African, or Persian)','Includes individuals who self-report or were described by authors as Middle Eastern, North African, Persian, or one of the sub-populations from this region (e.g., Saudi Arabian). We note there is heterogeneity in this category with different degrees of admixture as well as levels of genetic isolation. We note that there does not appear to be a suitable reference population for this category and efforts are required to fill this gap.');
INSERT INTO met.population(code,name,documentation) VALUES('amlathis','Hispanic or Latin American','Includes individuals who either self-report or are described by authors as Hispanic, Latino, Latin American, or one of the sub-populations from this region. This category includes individuals with known admixture of primarily European, African, and Native American ancestries, though some may have also a degree of Asian (e.g., Peru). We also note that the levels of admixture vary depending on the country, with Caribbean countries carrying higher levels of African admixture when compared to South American countries, for example. This category also includes individuals who genetically cluster with reference populations from this region, for example, 1000 Genomes and/or HapMap CLM, MXL, PEL, and PUR populations.');
INSERT INTO met.population(code,name,documentation) VALUES('amnat','Native American','Includes indigenous individuals of North, Central, and South America, descended from the original human migration into the Americas from Siberia. We note that there does not appear to be a suitable reference population for this category and efforts are required to fill this gap.');
INSERT INTO met.population(code,name,documentation) VALUES('unrep','Not reported','Includes individuals for which no ancestry or country of recruitment information is available.');
INSERT INTO met.population(code,name,documentation) VALUES('oce','Oceanian','Includes individuals that either self-report or have been described by authors as Oceanian or one of the sub-populations from this region (e.g., Native Hawaiian). We note that there does not appear to be a suitable reference population for this category and efforts are required to fill this gap.');
INSERT INTO met.population(code,name,documentation) VALUES('oth','Other','Includes individuals where an ancestry descriptor is known but insufficient information is available to allow assignment to one of the other categories.');
INSERT INTO met.population(code,name,documentation) VALUES('amixo','Other admixed ancestry','Includes individuals who either self-report or have been described by authors as admixed and do not fit the definition of the other admixed categories already defined (“African American or Afro-Caribbean” or “Hispanic or Latin American”).');
INSERT INTO met.population(code,name,documentation) VALUES('sas','South Asian','Includes individuals who either self-report or have been described by authors as South Asian or one of the sub-populations from this region (e.g., Asian Indian). This category also includes individuals who genetically cluster with reference populations from this region, for example, 1000 Genomes and/or HapMap BEB, GIH, ITU, PJL, and STU populations.');
INSERT INTO met.population(code,name,documentation) VALUES('seas','South East Asian','Includes individuals who either self-report or have been described by authors as South East Asian or one of the sub-populations from this region (e.g., Vietnamese). This category also includes individuals who genetically cluster with reference populations from this region, for example, 1000 Genomes KHV population. We note that East Asian and South East Asian populations are often conflated. However, recent studies indicate a unique genetic background for South East Asian populations.');
INSERT INTO met.population(code,name,documentation) VALUES('afrss','Sub-Saharan African','Includes individuals who either self-report or have been described by authors as Sub-Saharan African or one of the sub-populations from this region (e.g., Yoruban). This category also includes individuals who genetically cluster with reference populations from this region, for example, 1000 Genomes and/or HapMap ESN, LWK, GWD, MSL, MKK, and YRI populations.');
INSERT INTO met.population(code,name,documentation) VALUES('multi','Multiple populations','Category to represent combinations of multiple populations which may correspond tother populations referred to in the database.');
